import './App.css';
import { BrowserRouter as Router , Switch , Route} from 'react-router-dom';
import HeaderComponent from './components/HeaderComponent';
import RecommendedComponent from './components/RecommendedComponent';
import FooterComponent from './components/FooterComponent';
import HomeComponent from './components/HomeComponent';
import { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      Product_name: '',
      Price: 0,
      weight: '',
      description: '',
      quantity: 0
    }
    this.create = this.create.bind(this);
  }
    create(e) {
      // add entity - POST
      e.preventDefault();
      // creates entity
      fetch("http://localhost:3000/products/add", {
        "method": "POST",
        "headers": {
          "content-type": "application/json",
          "accept": "application/json"
        },
        "body": JSON.stringify({
          Product_name: this.state.Product_name,
          Price: this.state.Price,
          weight: this.state.weight,
          description: this.state.description,
          quantity: this.state.quantity,
        })
      })
      .then(response => response.json())
      .then(response => {
        console.log(response)
      })
      .catch(err => {
        console.log(err);
      });
    }

    changeNameHandler = (event) => {
      this.setState
    }


  render() {
  return (
    <>
    <HeaderComponent />
    <input placeholder='Name' name='Product_name' value={this.state.Product_name} />
    <input placeholder='Name' name='Product_name' value={this.state.Product_name} />
    <button onClick={this.create}>Test</button>
      <Router>
        <Switch>
          <Route path='/products' component={RecommendedComponent}></Route>
          <Route path='/' component={HomeComponent}></Route>
        </Switch>
      </Router>
    <FooterComponent />
    </>

  )
}
}
export default App;
