import React, {Component, useState, useEffect} from "react";
import { useSearchParams } from 'react-router-dom'

function ToggleButtonExample() {
    const [checked, setChecked] = useState(false);
    const [radioValue, setRadioValue] = useState('1');
  
    const radios = [
      { name: 'Active', value: '1' },
      { name: 'Radio', value: '2' },
      { name: 'Radio', value: '3' },
    ];
    return (
        <>
          <ButtonGroup className="mb-2">
            <ToggleButtonExample
              id="toggle-check"
              type="checkbox"
              variant="secondary"
              checked={checked}
              value="1"
              onChange={(e) => setChecked(e.currentTarget.checked)}
            >
              Checked
            </ToggleButtonExample>
          </ButtonGroup>
          <br />
          <ButtonGroup className="mb-2">
            {radios.map((radio, idx) => (
              <ToggleButtonExample
                key={idx}
                id={`radio-${idx}`}
                type="radio"
                variant="secondary"
                name="radio"
                value={radio.value}
                checked={radioValue === radio.value}
                onChange={(e) => setRadioValue(e.currentTarget.value)}
              >
                {radio.name}
              </ToggleButtonExample>
            ))}
          </ButtonGroup>
          <br />
          <ToggleButtonExample
            className="mb-2"
            id="toggle-check"
            type="checkbox"
            variant="outline-primary"
            checked={checked}
            value="1"
            onChange={(e) => setChecked(e.currentTarget.checked)}
          >
            Checked
          </ToggleButtonExample>
          <br />
          <ButtonGroup>
            {radios.map((radio, idx) => (
              <ToggleButtonExample
                key={idx}
                id={`radio-${idx}`}
                type="radio"
                variant={idx % 2 ? 'outline-success' : 'outline-danger'}
                name="radio"
                value={radio.value}
                checked={radioValue === radio.value}
                onChange={(e) => setRadioValue(e.currentTarget.value)}
              >
                {radio.name}
              </ToggleButtonExample>
            ))}
          </ButtonGroup>
        </>
      );
    }
    

function IncDecCounter(){
    let [num, setNum]= useState(0);
    let incNum =()=>{
      if(num<100)
      {
      setNum(Number(num)+1);
      }
    };
    let decNum = () => {
       if(num>0)
       {
        setNum(num - 1);
       }
    }
   let handleChange = (e)=>{
     setNum(e.target.value);
    }

    return(
        <>
        <div className="col-x2-2">
        <div class="input-group">
      <div class="input-group-prepend">
        <button class="btn btn-danger" type="button" onClick={decNum}>-</button>
      </div>
      <input type="text" class="form-control" value={num} onChange={handleChange}/>
      <div class="input-group-prepend">
        <button class="btn btn-success" type="button" onClick={incNum}>+</button>
      </div>
    </div>
    </div>
       </>
      );
    }

function CardComponent(props){
    
    console.log(props.quantity)
    return (<div class="col-xl-2 col-lg-3 col-md-4 col-6">
    
    <div class="card card-sm card-product-grid">
        <a href="#" class="img-wrap"> <img src={`${props.image}`} /> </a>
        <figcaption class="info-wrap">
             { !props.quantity && <div class="badge bg-danger ms-2 w-40"><small class="text-white">Out Of Stock</small></div>}
            <a href="#" class="title">{props.name}</a>
            <div class="price mt-1">${props.price}</div>
            <IncDecCounter /> 
        </figcaption>
    </div>
</div>
)
}

function PaginationComponent(props) {
    
    

    var [page,setPage,num_of_pages] = [props.page ,props.setPage , props.num_of_pages]
    function chagepage(event){
        var id = event.target.id
        setPage(parseInt(id))
    }
    var pagination = []
    var class_ = page===1? "page-item disabled": "page-item"
    var element = <li className={class_}><button class="page-link" onClick={function (){
        setPage(page-1)
    }}>Previous</button></li>
    pagination.push(element)
    var start = page<=num_of_pages-2? page:num_of_pages-2
    for (var i=start;i<=start+2;i++){  
        element = <li className="page-item"><button class="page-link" id={i} onClick={chagepage} >{i}</button></li>
        pagination.push(element)
    }
    class_ = page===num_of_pages? "page-item disabled": "page-item"
    element = <li className={class_}><button class="page-link" onClick={function (){
        setPage(page+1)
    }}>Next</button></li>
    pagination.push(element)
    
    return (
        <ul class="pagination justify-content-center">
            {pagination}
        </ul>        
    )
}



function ProductsComponent(props) {
    let [Products, setProducts] = useState([]);
    let [page,setPage] = useState(1);
    let [paginationlist,setPaginationlist] = useState([]);
    let num_of_pages = props.pages
    // const [searchParams, setSearchParams] = useSearchParams();
    // console.log(props.match.params.name)
    const cards_per_page =12
    var search = window.location.href.split('/').at(-1)
    console.log(search)
    useEffect( () => {
        // item.quantity
        // const name = searchParams.get('name')
        // const src = searchParams.get('src')
        var url = search === "products"? `http://localhost:3000/products?page=${page}` :`http://localhost:3000/product/${search}?page=${page}` 
        fetch(url, {
        "method": "GET"
        })
        .then(response => response.json())
        .then(response => {
            setProducts(response.slice((page-1)*cards_per_page,page*cards_per_page).map((item)=> <CardComponent quantity={item.quantity}  name={item.Product_name} price={item.Price} image="assets/images/items/2.jpg"/>))
            return Products
        }).then(data => {
            // console.log(data)               
        }).catch(err => { console.log(err); 
        }
        ) }
        , [page,search]
    
    );

    

    //  console.log(Products)
    return (
        <div className="Container">
            <div className="row pt-3">
            { Products }
            </div>
            <div>
                <PaginationComponent key={1} page={page}  setPage={setPage} num_of_pages={num_of_pages}/>              
            </div>
        </div>
    )
}


export default ProductsComponent;
