import React, {Component,useState} from "react";
import { browserHistory } from 'react-router';
import { NavLink  } from 'react-router-dom';
import { Link } from 'react-router-dom';

function HeaderComponent() {

    let [value, setState] = useState([]);

    function handleSubmit(event) {
        event.preventDefault();
        if(value){
            window.location.replace(`http://localhost:3006/products/${value}`)

        }

        
        // browserHistory.push("/path");
    }

    function handleChange(event) {
        setState(event.target.value)
        console.log(event.target.value)
    }

    return (
        <header class="section-header">
            <section class="header-main border-bottom">
                <div class="container">
                    <div class="row align-items-center">
                        <div class="col-xl-2 col-lg-3 col-md-12">
                            
                            <a href="/" class="widget-view text-muted">
                                <i class="fa-solid fa fa-home fa-lg text-muted"></i> 
                            </a> 
                        </div>
                        <div class="col-xl-6 col-lg-5 col-md-6">
                            <form class="search-header">
                            {/* <form action=""  class="search-header"> */}
                                <div class="input-group w-100 ">
                                    <select class="custom-select border-right"  name="category_name">
                                            <option value="">All type</option><option value="codex">Special</option>
                                            <option value="comments">Only best</option>
                                            <option value="content">Latest</option>
                                    </select>
                                    <input type="text" class="form-control" placeholder="Search" value={value} onChange={handleChange} />
                                    <div class="input-group-append">
                                    <button  onSubmit={handleSubmit} class="btn btn-primary" type="submit">
                                        <i class="fa fa-search"></i> Search
                                    </button>
                                    </div>
                                </div>
                            </form>
                        </div> 
                        <div class="col-xl-4 col-lg-4 col-md-6">
                            <div class="widgets-wrap float-md-right">
                                <div class="widget-header mr-3">
                                <Link to="/products" class="widget-view">
                                    {/* <a href="/products" class="widget-view"> */}
                                        <div class="icon-area">
                                            <i class="fa fa-shopping-bag"></i>
                                        </div>
                                        <small class="text"> Products </small>
                                    {/* </a> */}
                                </Link>
                                </div>
                                <div class="widget-header mr-3">
                                    <a href="#" class="widget-view">
                                        <div class="icon-area">
                                            <i class="fa fa-store"></i>
                                        </div>
                                        <small class="text"> Orders </small>
                                    </a>
                                </div>
                                <div class="widget-header">
                                <Link to="/cart" class="widget-view">
                                    <div class="icon-area">
                                        <i class="fa fa-shopping-cart"></i>
                                    </div>
                                    <small class="text"> Cart </small>
                                </Link>
                                    {/* <a href="/Cart" class="widget-view">
                                        <div class="icon-area">
                                            <i class="fa fa-shopping-cart"></i>
                                        </div>
                                        <small class="text"> Cart </small>
                                    </a> */}
                                </div>
                            </div> 
                        </div>
                    </div>
                </div> 
            </section> 
            
            </header> 
    )

}


