import './App.css';
import { BrowserRouter as Router , Switch , Route} from 'react-router-dom';
import HeaderComponent from './components/HeaderComponent';
import RecommendedComponent from './components/RecommendedComponent';
import FooterComponent from './components/FooterComponent';
import HomeComponent from './components/HomeComponent';
import ProductsComponent from './components/ProductsComponent';

import React, {Component, useState, useEffect} from "react";
import Cart from './components/CartComponent';


// export const addToCart = (id, productId, quantity) => dispatch => {
//   axios.post(`/api/cart/${id}`, {productId, quantity})
//       .then(res => dispatch({
//           type: String,
//           payload: res.data
//       }))
//       // .catch(err => dispatch(returnErrors(err.response.data, err.response.status)));
// }

class App extends Component {


  constructor(props) {
    super(props);

      this.state = {
      // Product_name: '',
      // Price: 0,
      // weight: '',
      // description: '',
      // quantity: 0,
      cart : [
        {
        description: "Groovy Puppy Biscuits",
        id: "6296ab6d4f6f77f5ba4acabc",
        image: "assets/images/items/2.jpg",
        name: "Puppy Biscuits",
        price: 47,
        quantity: 1
        }
      ],
      // Email:'',
      // orderedproducts:'',
      // Status:'',
      // orderedShipping:''

    }

    this.create = this.create.bind(this);
    console.log('constructor')
  }



  getData() {
    // get all entities - GET
    fetch("http://localhost:3000/products", {
      "method": "GET"
    })
    .then(response => response.json())
    .then(response => {
      console.log(response);
    })
    .catch(err => { console.log(err); 
    });
  }

    create(e) {
      // add entity - POST
      e.preventDefault();
      // creates entity
      fetch("http://localhost:3000/products/add", {
        "method": "POST",
        "headers": {
          "content-type": "application/json",
          "accept": "application/json"
        },
        "body": JSON.stringify({
          Product_name: this.state.Product_name,
          Price: this.state.Price,
          weight: this.state.weight,
          description: this.state.description,
          quantity: this.state.quantity,
        })
      })
      .then(response => response.json())
      .then(response => {
        console.log(response)
      })
      .catch(err => {
        console.log(err);
      });
    }



    changeNameHandler = (event) => {
      this.setState({Product_name: event.target.value});
    }
    changePriceHandler = (event) => {
      this.setState({Price: event.target.value});
    }
    changeWeightHandler = (event) => {
      this.setState({weight: event.target.value});
    }
    changeDescriptionHandler = (event) => {
      this.setState({description: event.target.value});
    }

    change = (new_cart) => {
      this.setState({
        cart: new_cart 
      })
      console.log(this.state)
    }

    get_items = () => {
      return this.state.cart
    }

  render() {
  return (
    <>

      <Router>
      <HeaderComponent />
      <Switch>
        <Route exact path='/products/:parm1'><ProductsComponent pages = {6} get_items={this.get_items} change_item={this.change} /></Route>
        <Route exact path='/products'><ProductsComponent pages = {10} get_items={this.get_items} change_item={this.change} /></Route>
        <Route exact path='/' component={HomeComponent}></Route>
        <Route exact path='/cart'><Cart items={this.state.cart} /></Route>
      </Switch>
      </Router>
    <FooterComponent />
    </>

  )
}
}
export default App;
