import './App.css';
import { BrowserRouter as Router , Switch , Route} from 'react-router-dom';
import HeaderComponent from './components/HeaderComponent';
import RecommendedComponent from './components/RecommendedComponent';
import FooterComponent from './components/FooterComponent';
import HomeComponent from './components/HomeComponent';
import ProductsComponent from './components/ProductsComponent';
import CartComponent from './components/CartComponent';
import React, {Component, useState, useEffect} from "react";
import Cart from './components/CartComponent';
import { CartProvider } from "react-use-cart";






class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      Product_name: '',
      Price: 0,
      weight: '',
      description: '',
      quantity: 0,
      Email:'',
      orderedproducts:'',

    }
    this.create = this.create.bind(this);
  }

  getData() {
    // get all entities - GET
    fetch("http://localhost:3000/products", {
      "method": "GET"
    })
    .then(response => response.json())
    .then(response => {
      console.log(response);
    })
    .catch(err => { console.log(err); 
    });
  }

    create(e) {
      // add entity - POST
      e.preventDefault();
      // creates entity
      fetch("http://localhost:3000/products/add", {
        "method": "POST",
        "headers": {
          "content-type": "application/json",
          "accept": "application/json"
        },
        "body": JSON.stringify({
          Product_name: this.state.Product_name,
          Price: this.state.Price,
          weight: this.state.weight,
          description: this.state.description,
          quantity: this.state.quantity,
        })
      })
      .then(response => response.json())
      .then(response => {
        console.log(response)
      })
      .catch(err => {
        console.log(err);
      });
    }

    getData() {
      // get all entities - GET
      fetch("http://localhost:3000/orders", {
        "method": "GET"
      })
      .then(response => response.json())
      .then(response => {
        console.log(response);
      })
      .catch(err => { console.log(err); 
      });
    }
    create(e) {
      // add entity - POST
      e.preventDefault();
      // creates entity
      fetch("http://localhost:3000/orders/add", {
        "method": "POST",
        "headers": {
          "content-type": "application/json",
          "accept": "application/json"
        },
        "body": JSON.stringify({
          Email: this.state.Email,
          orderedproducts: this.state.orderedproducts,
          Status: this.state.Status,
          orderedShipping: this.state.orderedShipping,
        })
      })
      .then(response => response.json())
      .then(response => {
        console.log(response)
      })
      .catch(err => {
        console.log(err);
      });
    }

    changeNameHandler = (event) => {
      this.setState({Product_name: event.target.value});
    }
    changePriceHandler = (event) => {
      this.setState({Price: event.target.value});
    }
    changeWeightHandler = (event) => {
      this.setState({weight: event.target.value});
    }
    changeDescriptionHandler = (event) => {
      this.setState({description: event.target.value});
    }
    changeQuantityHandler = (event) => {
      this.setState({quantity: event.target.value});
    }


  render() {
  return (
    <>
    <HeaderComponent />
      <Router>
        <Switch>
        <Route exact path='/cart'> <Cart/></Route>
        <Route exact path='/products/:parm1'><ProductsComponent pages = {6} /></Route>
        <Route exact path='/products'><ProductsComponent pages = {10} /></Route>
        <Route exact path='/' component={HomeComponent}></Route>
        </Switch>
      </Router>
    <FooterComponent />
    </>

  )
}
}
export default App;
