function Cart(props) {

    let [num, setNum]= useState(1);
    let incNum =()=>{
      if(num<100)
      {
      setNum(Number(num)+1);
      }
    };
    let decNum = () => {
       if(num>1)
       {
        setNum(num - 1);
       }
    }
   let handleChange = (e)=>{
     setNum(e.target.value);
    }

    function removeelement(name2){
        document.getElementById(name2).remove();
        for (let index = 0; index < (props.items).length; index++) {
            if(props.items[index].name == name2)
            {
                (props.items).splice(index, 1);
            }
        }
    }
    function totalprice()
    {
        for (let index = 0; index < (props.items).length; index++) {
            document.getElementById("totalprice").value += Number(props.items[index].price)
        }
    }

    const listItems = props.items.map((link) =>
    <>
        {/* <li key={link.name}>{link.name}</li> 
        <li key={link.description}>{link.description}</li>
        <li key={link.price}>{link.price}</li>
        <li key={link.quantity}>{link.quantity}</li> */}
    <div onLoad={totalprice} id={link.name} class="card card-sm card-product-grid">
        <a class="img-wrap"> <img key={link.image} src={`${link.image}`} /></a>
        <figcaption class="info-wrap">            
            <a key={link.name} class="title">{link.name}</a>
            <a key={link.description} class="title text-info"> Description: {link.description}</a>
            <div key={link.price} class="price mt-1">${link.price}</div>
            {/* <div key={link.quantity} class="price mt-1">Quantity: {link.quantity}</div> */}
            <div className="col-x2-2">
        <div class="input-group">
      <div class="input-group-prepend">
        <button class="btn btn-outline-danger" type="button" onClick={decNum} class2="btn btn-danger" >-</button>
      </div>
      <input type="text" class="form-control" value={num} onChange={handleChange}/>
      <div class="input-group-prepend">
        <button class="btn btn-outline-success" type="button" onClick={incNum} class2="btn btn-success">+</button>
      </div>
      <button id={link.name} class="btn btn-outline-danger" type="button" onClick={() => removeelement(link.name)} class2="btn btn-success">Remove</button>
    </div>
    </div>
        </figcaption>
    </div>
    </>
    );

  return (
    <div>
        {/* {listItems}
        <CartComponent></CartComponent> */}
    </div>
    
  )

}

export default Cart;
