import React, { useState } from "react";
import { ObjectID } from 'bson';

function Cart(props) {
    let [orders, setorders] = useState([]);
    useEffect( () => {
        // item.quantity
        // const name = searchParams.get('name')
        // const src = searchParams.get('src')
        var url = 
        fetch(url, {
        "method": "GET"
        })
        .then(response => response.json())
        .then(response => {
            setProducts(response.slice((page-1)*cards_per_page,page*cards_per_page).map((item)=> <CardComponent onClick={add_to_cart} description={item.description} quantity={item.quantity} id={item._id}  name={item.Product_name} price={item.Price} image={item.image}/>))
            return Products
        }).then(data => {
            // console.log(data)               
        }).catch(err => { console.log(err); 
        }
        ) }
        , [page,search]
    
    );
    const postOrder = (e) => {
        e.preventDefault();
        var products = [];
        const shipid = new ObjectID();
        console.log(shipid.toString());
        for (let index = 0; index < (props.items).length; index++) {
            products.push(props.items[index].id);
        }
        console.log(products);
        if (document.getElementById("email").value == "") {
            alert("Please enter your email");
            return;
        }
        fetch('http://localhost:3000/orders/add', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                Email: document.getElementById("email").value,
                address : document.getElementById("address").value,
                orderedproducts: products,
                Status: 'CREATED',
                orderedShipping: shipid,
                totalPrice: totalprice()
            })
        })
        alert("Order placed successfully");
    }

    // let [num, setNum] = useState(1);
    // let incNum = () => {
    //     if (num < 100) {
    //         setNum(Number(num) + 1);
    //     }
    // };
    // let decNum = () => {
    //     if (num > 1) {
    //         setNum(num - 1);
    //     }
    // }
    // let handleChange = (e) => {
    //     setNum(e.target.value);
    // }
    
    function totalprice() {
        var total = 0;
        for (let index = 0; index < (props.items).length; index++) {
            total += (props.items[index].price);
        }
        return total;
    }

    function removeelement(name2) {
        document.getElementById(name2).remove();
        for (let index = 0; index < (props.items).length; index++) {
            if (props.items[index].name == name2) {
                (props.items).splice(index, 1);
            }
        }
        totalprice = totalprice- name2.Price;
    }

    const listItems = props.items.map((link) =>
        <div className="col-xl-2 col-lg-3 col-md-4 col-6">
            <div id={link.name} className="card card-sm card-product-grid">
                <a className="img-wrap"> <img key={link.image} src={`${link.image}`} /></a>
                <figcaption className="info-wrap">
                    <a key={link.name} className="title">{link.name}</a>
                    <a key={link.description} className="title text-info"> Description: {link.description}</a>
                    <div key={link.price} id="pricee" className="price mt-1">${link.price}</div>
                    <div className="col-x2-2">
                        <div className="input-group">
                            {/* <div className="input-group-prepend">
                                <button className="btn btn-outline-danger" type="button" onClick={decNum} class2="btn btn-danger" >-</button>
                            </div>
                            <input type="text" className="form-control" value={num} onChange={handleChange} />
                            <div className="input-group-prepend">
                                <button className="btn btn-outline-success" type="button" onClick={incNum} class2="btn btn-success">+</button>
                            </div> */}
                            <button id={link.name} className="btn btn-outline-danger" type="button" onClick={() => removeelement(link.name)} class2="btn btn-success">Remove</button>
                        </div>
                    </div>
                </figcaption>
            </div>
        </div>
    );
    
    return (
        <div onLoad={() => document.getElementById("totalprice").value = totalprice()} id="test">
            {listItems}
            <section className="container">
                <div className="judul">
                    <h4>Shopping Cart</h4>
                </div>
                <div className="row justify-content-center">
                    <div className="row">
                        <div >
                            <div className="card shadow p-3 mb-5 bg-body rounded">
                                <h5>Cart</h5>
                            </div>
                        </div>
                        <div>
                            <div className="card shadow p-3 mb-5 bg-body rounded">
                                {/* <h5>The total amount is</h5>
                                <div className="row">
                                    <div className="col-8">
                                        Temporary Amount
                                    </div>
                                    <div className="col-4">
                                    </div>
                                </div>

                                <div className="row">
                                    <div className="col-8">
                                        Shipping
                                    </div>
                                    <div className="col-4">
                                        Gratis
                                    </div> */}
                                {/* </div> */}
                                <hr></hr>

                                <div className="row">
                                    <div className="col-8">
                                        <h6> The total amount is<br></br> </h6>
                                    </div>
                                    <div className="col-8">
                                        <input className="col-4" id="totalprice" type="number" value="0" />
                                    </div>
                                </div>
                                <div className="col-12">
                                    <br></br>
                                    <h6>Email</h6>
                                    <input id="email" required />
                                    <br></br>
                                    <h6>Address</h6>
                                    <input id="address" required />

                                    <button onClick={postOrder} type="button" className="btn btn-primary check">GO TO CHECKOUT</button>
                                </div>
                            </div>
                            <div className="card shadow p-3 mb-5 bg-body rounded">
                                <h6>Add promo code(optional)</h6>
                                <input />
                                <button onClick={""} type="button" className="btn btn-primary check">ADD PROMO CODE</button>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>

    )

}

export default Cart;
