import React, {Component, useState, useEffect} from "react";

function CardComponent(props){
    return (<div class="col-xl-2 col-lg-3 col-md-4 col-6">
    <div class="card card-sm card-product-grid">
        <a href="#" class="img-wrap"> <img src='${props.image}' /> </a>
        <figcaption class="info-wrap">
            <a href="#" class="title">{props.name}</a>
            <div class="price mt-1">${props.price}</div> 
        </figcaption>
    </div>
</div>
)
}

function PaginationComponent(page,setPage,num_of_pages) {
    console.log(page,setPage,num_of_pages)
    var pagination = []
    var class_ = page===1? "page-item disabled": "page-item"
    var element = <li className={class_}><button class="page-link" onClick={function (){
        setPage(page-1)
    }}>Previous</button></li>
    pagination.push(element)
    var start = page<=num_of_pages-2? page:num_of_pages-2
    for (var i=start;i<=start+2;i++){  
        element = <li className="page-item"><button class="page-link" onClick={function (){
            setPage(i)
        }}>{i}</button></li>
        pagination.push(element)
    }
    class_ = page===num_of_pages? "page-item disabled": "page-item"
    element = <li className={class_}><button class="page-link" onClick={function (){
        setPage(page+1)
    }}>Next</button></li>
    pagination.push(element)
    
    return (
        <div>
            {pagination}
        </div>        
    )
}



function ProductsComponent(props) {
    let [Products, setProducts] = useState([]);
    let [page,setPage] = useState(1);
    let [paginationlist,setPaginationlist] = useState([]);
    let num_of_pages = props.pages
    // console.log(num_of_pages)
    //let Products = []

    // function createpagination () {
    //     var pagination = []
    //     var class_ = page===1? "page-item disabled": "page-item"
    //     var element = <li className={class_}><button class="page-link" onClick={function (){
    //         setPage(page-1)
    //     }}>Previous</button></li>
    //     pagination.push(element)
    //     var start = page<=num_of_pages-2? page:num_of_pages-2
    //     for (var i=start;i<=start+2;i++){  
    //         element = <li className="page-item"><button class="page-link" onClick={function (){
    //             setPage(i)
    //         }}>{i}</button></li>
    //         pagination.push(element)
    //     }
    //     class_ = page===num_of_pages? "page-item disabled": "page-item"
    //     element = <li className={class_}><button class="page-link" onClick={function (){
    //         setPage(page+1)
    //     }}>Nsext</button></li>
    //     pagination.push(element)
    //     return pagination
    // }


    useEffect( () => {
            
        fetch(`http://localhost:3000/products?page=${page}`, {
        "method": "GET"
        })
        .then(response => response.json())
        .then(response => {
            setProducts(response.slice((page-1)*10,page*10).map((item)=> <CardComponent  name={item.Product_name} price={item.Price} image="assets/images/items/2.jpg"/>))
            return Products
        }).then(data => {
            // console.log(data)               
        }).catch(err => { console.log(err); 
        }
        ) }
        , [page]
    
    );

    // useEffect( () => {
        
    //     setPaginationlist(createpagination())
    
    // }        
    // , [page]
    // );


    //  console.log(Products)
    return (
        <div className="Container">
            <div className="row pt-3">
            { Products }
            {/* /* <CardComponent  name="Strawberry" price="12.00" image="assets/images/items/2.jpg"/>
            <CardComponent  name="Strawberry" price="12.00" image="assets/images/items/2.jpg"/>
            <CardComponent  name="Strawberry" price="12.00" image="assets/images/items/2.jpg"/>
            <CardComponent  name="Strawberry" price="12.00" image="assets/images/items/2.jpg"/> */ }
            </div>

            <div>
                <ul class="pagination justify-content-center">
                <PaginationComponent page={page}  setPage={setPage} num_of_pages={num_of_pages}/>
                {/* {paginationlist} */}
                </ul>
            </div>
        </div>
    )
}


export default ProductsComponent;
