import React, { useState, useEffect } from "react";
import { useCart } from 'react-use-cart';
import { Card, CardBody } from "reactstrap";
import ProductsComponent from "./ProductsComponent";

function IncDecCounter(){
    let [num, setNum]= useState(0);
    let incNum =()=>{
      if(num<100)
      {
      setNum(Number(num)+1);
      }
    };
    let decNum = () => {
       if(num>0)
       {
        setNum(num - 1);
       }
    }
   let handleChange = (e)=>{
     setNum(e.target.value);
    }

    return(
        <>
        <div className="col-x2-2">
        <div class="input-group">
      <div class="input-group-prepend">
        <button class="btn btn-outline-danger" type="button" onClick={decNum} class2="btn btn-danger" >-</button>
      </div>
      <input type="text" class="form-control" value={num} onChange={handleChange}/>
      <div class="input-group-prepend">
        <button class="btn btn-outline-success" type="button" onClick={incNum} class2="btn btn-success">+</button>
      </div>
    </div>
    </div>
       </>
      );
    }

const CartComponent = (props) => {
    const { 
        isEmpty,
        totalUniqueItems,
        items,
        totalItems,
        cartTotal,
        updateItemQuantity, 
        removeItem,
        emptyCart,
         } = useCart();
    // if(isEmpty) return <h1 className="text-center">Your Cart is Empty</h1>
    return(
        <section className="container">
            <div className="judul">
                <h4>Shopping Cart</h4>
            </div>
            <div className="row justify-content-center">
                <div className="row">
                    <div >
                        <div className="card shadow p-3 mb-5 bg-body rounded">
                        <h5>Cart ({totalItems} cart)</h5>
                             {items.map((cart, index)=>{
                                 return(
                                    <div className="row">
                                        <div>
                                            <img src={cart.img} style={{height:'200px',marginLeft:'20%', marginTop:'5%'}} class="img-fluid rounded-start" alt={props.items.name}/>
                                            </div>
                                            <div>
                                            <div class="card-body">
                                                <div className="row align-items-start">
                                                    <div className="col-8"><h5 class="card-title">{cart.name}</h5></div>
                                                    <div className="col-4">
                                                        <div className="btn-group" role="group" >
                                                            <button type="button" class="btn btn-outline-dark"
                                                                onClick ={() => updateItemQuantity(cart.id, cart.quantity-1)}
                                                                >-</button>
                                                            <button type="button" disabled class="btn btn-outline-dark"
                                                            >  {cart.quantity}</button>
                                                            <button type="button" class="btn btn-outline-dark"
                                                                onClick ={() => updateItemQuantity(cart.id, cart.quantity+1)}
                                                            >+</button>
                                                        </div>
                                                    </div>
